package first;

public class AssignmentOpEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int y=a++;
		System.out.println(y);
		int z=++a;
		System.out.println(z);
		int x=a--;
		System.out.println(x);
		int m=--a;
		System.out.println(m);
		
		
	}

}
